        $(document).ready(function() {
            // Fungsi untuk mengupdate waktu
            function updateTimestamps() {
                var now = new Date().getTime() / 1000; // Waktu saat ini dalam detik
                $('.timestamp-hidden').each(function() {
                    var timePosted = parseInt($(this).data('time'));
                    var timeDiff = now - timePosted;
                    var timeAgo = 'baru saja';
                    
                    if (timeDiff < 60) {
                        timeAgo = 'baru saja';
                    } else if (timeDiff < 3600) {
                        timeAgo = Math.floor(timeDiff / 60) + ' menit yang lalu';
                    } else if (timeDiff < 86400) {
                        timeAgo = Math.floor(timeDiff / 3600) + ' jam yang lalu';
                    } else {
                        timeAgo = Math.floor(timeDiff / 86400) + ' hari yang lalu';
                    }
                    
                    $(this).prev('.timestamp').text(timeAgo);
                });
            }

            // Update waktu setiap detik
            setInterval(updateTimestamps, 1000);

            // Kirim formulir menggunakan AJAX
            $('#messageForm').on('submit', function(event) {
                event.preventDefault(); // Mencegah pengiriman formulir secara default

                $.ajax({
                    url: 'post_wishes.php', // URL endpoint untuk menangani data
                    type: 'POST',
                    data: $(this).serialize(), // Serialize data formulir
                    success: function(response) {
                        $('#messageContainer').prepend(response); // Menambahkan pesan baru ke bagian atas
                        $('#messageForm')[0].reset(); // Mengatur ulang formulir setelah pengiriman
                    },
                    error: function() {
                        $('#messageContainer').prepend('<p>Error occurred.</p>'); // Menampilkan pesan error jika terjadi masalah
                    }
                });
            });

            // Panggil fungsi updateTimestamps saat halaman dimuat
            updateTimestamps();
        });

