                            }
                            $('#tombolsimpan').prop('disabled', false);
                        },
                        error: function(xhr, status, error) {
                            $('#tombolsimpan').prop('disabled', false);
                            $('#tombolsimpan').html('Kirim');
                            console.error('Error:', error);
                            $('#responseMessage').html("<p class='text-danger'>Terjadi kesalahan. Silakan coba lagi nanti.</p>");
                        }
                    });
                }
            });
