                        function copyRekening() {
                            var rekening = document.getElementById("rekening").innerText;
                            navigator.clipboard.writeText(rekening).then(function() {
                                showNotification("Nomor rekening berhasil disalin!", 'success');
                            }).catch(function() {
                                showNotification("Gagal menyalin nomor rekening.", 'error');
                            });
                        }

                        function showNotification(message, type) {
                            var notification = document.getElementById("notification");
                            var messageElement = document.getElementById("notification-message");
                            var icon = notification.querySelector('.icon');

                            messageElement.textContent = message;
                            notification.className = 'notification ' + type;
                            icon.className = 'icon ' + type;

                            notification.style.display = 'flex';
                            notification.style.opacity = 1;

                            setTimeout(function() {
                                notification.style.opacity = 0;
                                setTimeout(function() {
                                    notification.style.display = 'none';
                                }, 500); // Match the CSS transition duration
                            }, 3000); // Display the notification for 3 seconds
                        }
                     
    </script>

